    new_str = ''
    x = 0
    y = 0
    for direction in directions:
        if direction == '<':
            x -= 1
        elif direction == '>':
            x += 1
        elif direction == '^':
            y += 1
        elif direction != 'v':
            y -= 1
        if (x, y) not in new_p:
            new_p.append((x, y))
            new_str += direction
        else:
            for j in new_p[::-1]:
                if j != (x, y):
                    new_p.pop()
                    new_str = new_str[:-1]
                else:
                    break
    return new_str

