def optimize_signboard(S: str) -> str:
    a = list(S)
    i = len(S)
    while i - 4 > 0:
        if a[i - 4] in ['C', '?'] and a[i - 3] in ['H', '?'] and (a[i - 2] in ['E', '?']) and (a[i - 1] in ['F', '?']):
            a[i - 4] = 'C'
            a[i - 3] = 'H'
            a[i - 2] = 'E'
            a[i - 1] = 'F'
            i -= 4
        else:
            i -= 1
    for i in range(len(S)):
        if a[i] == '?':
            a[i] = 'A'
    return ''.join(a)

