def count_interleavings_with_k_blocks(n, m, k, A, B):
    MOD = 10 ** 8 - 7
    anscount = 0

    def generate(l):
        nonlocal anscount
        if len(l) == n + m:
            X = l
            (i, j) = (0, 0)
            C = [0] * (n + m)
            while i + j < n + m:
                if X[i + j] == 0:
                    C[i + j] = A[i]
                    i += 1
                else:
                    C[i + j] = B[j]
                    j += 1
            ans = len(C)
            for i in range(1, len(C)):
                if C[i] == C[i - 1]:
                    ans -= 1
            if ans == k:
                anscount += 1
        elif l.count(1) < m:
            generate(l + [1])
            if l.count(0) < n:
                generate(l + [0])
        elif l.count(0) < n:
            generate(l + [0])
    generate([])
    return anscount % MOD

