def find_permutation_splits(t, test_cases):
    results = []
    for case in test_cases:
        (n, a) = case
        answer = 1
        q = []
        Max = 0
        ind = 0
        for j in range(n):
            if Max < a[j]:
                Max = a[j]
        ind = Max - 1
        a1 = a[:n - ind - 1]
        a2 = a[n - ind - 1:]
        a3 = a[:ind + 1]
        a4 = a[ind + 1:]
        a1.sort()
        a2.sort()
        a3.sort()
        a4.sort()
        f1 = True
        f2 = True
        for i in range(n - ind - 1):
            if a1[i] != i + 1:
                f1 = False
        if f1:
            for i in range(ind + 1):
                if a2[i] != i + 1:
                    f1 = False
        if f1:
            answer += 1
            q.append((n - ind - 1, ind + 1))
        for i in range(ind + 1):
            if a3[i] != i + 1:
                f2 = False
        if f2:
            for i in range(n - ind - 1):
                if a4[i] != i + 1:
                    f2 = False
        if f2 and n - ind - 1 != ind + 1:
            answer += 1
            q.append((ind + 1, n - ind - 1))
        results.append((answer, q))
    return results

