from itertools import permutations

def calculate_ways_to_defeat_enemies(N, powers):
    entrances = [0, 0]
    NumberOfWays = 1

    def sol(powers, index):
        nonlocal NumberOfWays
        if index == len(powers):
            NumberOfWays += 1
            return
        for i in range(2):
            if i == 0 and entrances[0] + powers[index] > entrances[1]:
                continue
            entrances[i] += powers[index]
            sol(powers, index + 1)
            entrances[i] -= powers[index]
    if N == 0:
        return (1, 0)
    TotalPowerOfEnemies = sum(powers)
    arrangements = list(map(list, permutations(powers)))
    for arrangement in arrangements:
        entrances = [0, 0]
        sol(arrangement, 0)
    return (NumberOfWays, TotalPowerOfEnemies)

