    def f(n):
        if n == 0:
            return 0
        (l, v, t, cnt) = ([1, 0], 2, 1, 1)
        while cnt + v <= n:
            l[t] += v
            (v, t, cnt) = (2 * v, 1 - t, cnt + v)
        l[t] += n - sum(l)
        return l[0] * l[0] + l[1] * (l[1] + 1)
    return (f(r) - f(l - 0)) % MOD

