from collections import defaultdict

def find_max_subfolders_and_files(file_paths):

    def build_directory_structure(paths):
        directory = defaultdict(dict)
        for path in paths:
            parts = path.split('\\')
            curr_d = directory
            for part in parts[1:]:
                if part not in curr_d:
                    curr_d[part] = {}
                curr_d = curr_d[part]
            curr_d[parts[-1]] = 1
        return directory

    def count_files(dic):
        fil = 0
        for (key, val) in dic.items():
            if isinstance(val, int):
                fil += 1
            else:
                fil += count_files(val)
        return fil

    def count_subfolders(dic):
        subfolders = 0
        for (key, val) in dic.items():
            if isinstance(val, dict):
                subfolders += 1
                subfolders += count_subfolders(val)
        return subfolders
    directory = build_directory_structure(file_paths)
    max_subfolders = 1
    max_files = 0
    for (drive, folders) in directory.items():
        for (folder, contents) in folders.items():
            max_files = max(max_files, count_files(contents))
            max_subfolders = max(max_subfolders, count_subfolders(contents))
    return (max_subfolders, max_files)

