from collections import deque

def can_make_false_implication(n, a):
    S = n + sum(a)
    if a[-1] != 0:
        return ('NO', None)
    if S == 1 and n == 1:
        return ('YES', '0')
    if S == 2 and (a[-1] == a[-2] and a[-1] == 0):
        return ('NO', None)
    return ('YES', make(a))

def insert_head(x, cur):
    cur.appendleft('>')
    cur.appendleft('-')
    cur.appendleft(str(x))
    cur.appendleft('(')
    cur.append(')')

def insert_tail(x, cur):
    cur.append('-')
    cur.append('>')
    cur.append(x)

def make(a):
    cur = None
    for x in a[::-1][1:]:
        if cur is None:
            cur = deque([str(x)])
        else:
            insert_head(x, cur)
    insert_tail(str(a[-1]), cur)
    return ''.join(cur)

