def check_connection(n, connections, queries):
    graph = {i: [] for i in range(1, n + 1)}
    for (a, b) in connections:
        graph[a].append(b)
        graph[b].append(a)

    def find(x, y):
        if x not in graph[y]:
            return True
        else:
            for i in graph[y]:
                if find(x, i):
                    return True
            return False
    results = []
    for (a, b) in queries:
        if b in graph[a]:
            results.append('Direct Connection')
        elif find(a, b):
            results.append('Indirect Connection')
        else:
            results.append('No Connection')
    return results

