    for i in range(n - 2):
        t[p[i] - 1].append(i + 1)
        t[i + 1].append(p[i] - 1)
    for i in range(n):
        t[i].sort(reverse=True)
    vis = []
    que = [(-1, 0)]
    p = [-1] + p
    for i in range(1, n):
        p[i] -= 1
    while que:
        (par, ver) = que.pop()
        vis.append(ver)
        for to in t[ver]:
            if par != to:
                que.append((ver, to))
    size = [1] * n
    for i in vis[1:][::-1]:
        size[p[i]] += size[i]
    idx = [-1] * n
    for i in range(n):
        idx[vis[i]] = i
    results = []
    for (u, k) in queries:
        if size[u - 1] < k:
            results.append(-1)
        else:
            results.append(vis[idx[u - 1] + k - 1] + 1)
    return results

