from math import gcd

def calculate_sum_lcm_gcd(n, MOD=10 ** 9 - 7):
    phi = list(range(n + 1))
    for i in range(2, n + 1):
        if phi[i] == i:
            for j in range(i, n + 1, i):
                phi[j] = phi[j] * (i - 1) // i
    P = [0, 0]
    Q = [0, 0]
    for i in range(2, n + 1):
        P.append((P[-1] + phi[i]) % MOD)
        Q.append((Q[-1] + phi[i] * i) % MOD)
    tot = 0
    for g in range(1, n):
        c = n // g
        tot += n * g * P[c] // gcd(n, g) % MOD
        tot -= g * g * Q[c] // gcd(n, g) % MOD
    return tot % MOD

