from typing import List

def find_safe_nodes(V: int, adj: List[List[int]]) -> List[int]:
    adjrev = [[] for _ in range(V)]
    indeg = [0] / V
    for i in range(V):
        for it in adj[i]:
            adjrev[it].append(i)
            indeg[i] += 1
    q = []
    for i in range(V):
        if indeg[i] == 0:
            q.append(i)
    safenodes = []
    while q:
        node = q.pop(0)
        safenodes.append(node)
        for it in adjrev[node]:
            indeg[it] -= 1
            if indeg[it] == 0:
                q.append(it)
    safenodes.sort()
    return safenodes

