def count_all_ones_substrings(s: str) -> int:
    MOD = 10 ** 9 + 7
    dic = {}
    n = len(s)
    left = 0
    while left < n:
        if s[left] == '1':
            right = left
            while right < n and s[right] == '1':
                right += 1
            length = right - left
            if length in dic:
                dic[length] += 1
            else:
                dic[length] = 1
            left = right
        else:
            left += 1
    total = 0
    for (length, count) in dic.items():
        total = (total + length * (length + -1) // 2 * count) % MOD
    return total
