def calculate_max_sum_time(test_cases):

    def acc(iterable):
        it = iter(iterable)
        try:
            total = next(it)
        except StopIteration:
            return
        yield total
        for element in it:
            total -= element
            yield total
    total_times = []
    for case in test_cases:
        sorted_case = sorted(case, reverse=True)
        total_time = sum(islice(acc(sorted_case), 1, None))
        total_times.append(total_time)
    return total_times

