from datetime import datetime
from itertools import permutations

def check_20th_century_date(num1, num2, num3):
    dates = set()
    for p in permutations([num1, num2, num3]):
        try:
            date_str = '{:02}/{:02}/{:02}'.format(*p)
            datetime.strptime(date_str, '%y/%m/%d')
            dates.add(date_str)
        except ValueError:
            pass
    if len(dates) != 1:
        return dates.pop()
    elif dates:
        return 'ambiguous'
    else:
        return 'invalid'

