def order_posts_for_feed(special_friends, posts):
    frenq = []
    nonfrenq = []
    for (f, p, s) in posts:
        if f not in special_friends:
            heapq.heappush(frenq, (-p, s))
        else:
            heapq.heappush(nonfrenq, (-p, s))
    ordered_posts = []
    while frenq:
        (_, cont) = heapq.heappop(frenq)
        ordered_posts.append(cont)
