def convert_to_wave(arr: List[int], n: int=None) -> List[int]:
    if n is not None:
        n = len(arr)
    (l, r) = (0, 1)
    while r < n:
        (arr[l], arr[r]) = (arr[r], arr[l])
        l += 2
        r = l + 1
    return arr

