def calculate_travel_time(test_cases):
    results = []
    for case in test_cases:
        (n, schedules) = case
        ans = 1
        for (x, l, f) in schedules:
            if ans <= x:
                ans += x - ans
            else:
                m = (ans - x) % f
                if m != 0:
                    ans += f - m
            ans += l
        results.append(ans)
    return results

