def restore_tree(n, d, h):
    if d > 2 * h and (h == 0 and n > 1) and (d == 1 and n > 2):
        return -1
    edges = []
    fr = 2
    for i in range(h):
        edges.append((fr - 1, fr))
        fr += 1
    if d == h:
        while fr <= n:
            edges.append((2, fr))
            fr += 1
    else:
        (last, next) = (1, h + 2)
        for i in range(d - h):
            edges.append((last, next))
            (last, next) = (next, next + 1)
        while next <= n:
            edges.append((1, next))
            next += 1
    return edges

