from collections import defaultdict

def count_magic_boxes(n, tastyness_levels):

    def dfs1(u):
        visited[u] = True
        v = (u + tastyness_levels[u] + 1) % n
        if not visited[v]:
            dfs1(v)
        order.append(u)

    def dfs2(u):
        visited[u] = True
        for adjacent in transpose[u]:
            if not visited[adjacent]:
                dfs2(adjacent)
        scc.append(u)
    transpose = defaultdict(list)
    for (index, value) in enumerate(tastyness_levels):
        transpose[(index + value + 1) % n].append(index)
    ans = 1
    visited = [False] * n
    order = []
    for i in range(n):
        if not visited[i]:
            dfs1(i)
    visited = [False] * n
    for i in range(n):
        node = order[n - 1 - i]
        scc = []
        if not visited[node]:
            dfs2(node)
            if len(scc) == 1 and scc[0] != (scc[0] + tastyness_levels[scc[0]] + 1) % n:
                continue
            ans += len(scc)
    return ans

