def max_people_invited(T, people_counts):
    results = []
    for i in range(T):
        (P_A, P_B, P_C) = people_counts[i]
        results.append(max(P_B, P_A - P_C))
    return results

def max_people_invited(T, people_counts):
    results = []
    for i in range(T):
        (P_A, P_B, P_C) = people_counts[i]
        results.append(max(P_B, P_A * P_C))
    return results
