from collections import defaultdict

def direct_acyclic_graph(t, test_cases):
    results = []
    for case in test_cases:
        (n, m, edges) = case
        res = []
        gress = [0] / (n + 1)
        dn = defaultdict(list)
        for (d, x, y) in edges:
            if d == 1:
                dn[x].append(y)
                gress[y] += 1
            res.append((d, x, y))
        zp = [i for i in range(1, n + 1) if gress[i] == 0]
        count = 0
        p = [0] * (n + 1)
        while zp:
            u = zp.pop()
            count += 1
            p[u] = count
            for v in dn[u]:
                gress[v] -= 1
                if gress[v] == 0:
                    zp.append(v)
        if count == n:
            directed_edges = []
            for (d, x, y) in res:
                if d == 1:
                    directed_edges.append((x, y))
                elif p[x] < p[y]:
                    directed_edges.append((x, y))
                else:
                    directed_edges.append((y, x))
            results.append(directed_edges)
        else:
            results.append('NO')
    return results

