def assign_t_shirts(t_shirt_counts, participants):
    size_index = {'': 0, 'M': 1, 'L': 2, 'XL': 3, 'XXL': 4}
    index_size = {0: 'S', 1: 'M', 2: 'L', 3: 'XL', 4: 'XXL'}
    assigned_sizes = []
    for participant in participants:
        optimal_size = size_index[participant]
        if t_shirt_counts[optimal_size] > 0:
            t_shirt_counts[optimal_size] -= 1
            assigned_sizes.append(index_size[optimal_size])
        else:
            found = False
            for offset in range(1, 5):
                if optimal_size + offset < 5 and t_shirt_counts[optimal_size + offset] > 0:
                    t_shirt_counts[optimal_size + offset] -= 1
                    assigned_sizes.append(index_size[optimal_size + offset])
                    found = True
                    break
                elif optimal_size - offset >= 0 and t_shirt_counts[optimal_size - offset] > 0:
                    t_shirt_counts[optimal_size - offset] -= 1
                    assigned_sizes.append(index_size[optimal_size - offset])
                    found = True
                    break
            if not found:
                raise ValueError('No T-shirt available for participant')
    return assigned_sizes

