def can_transform_words(s: str, t: str) -> str:
    if t not in s:
        return 'automaton'
    cnt1 = [0] * 26
    cnt2 = [0] * 26
    for char in s:
        cnt1[ord(char) - ord('a')] += 1
    for char in t:
        cnt2[ord(char) - ord('a')] += 1
    array_possible = True
    for i in range(26):
        if cnt2[i] > cnt1[i]:
            array_possible = False
            break
    if not array_possible:
        return 'need tree'
    (i, j) = (0, 0)
    while i < len(s) and j < len(t):
        if s[i] == t[j]:
            j += 1
        i += 1
    if j == len(t):
        return 'automaton'
    elif len(s) == len(t):
        return 'array'
    else:
        return 'both'

