    sa = sum(a) + p
    sb = sum(b)
    if sa <= 0:
        return -1
    AB = sorted([(a[i], b[i]) for i in range(n)], key=lambda x: -(x[1] / x[0]))
    sa = sum(a) - p
    sb = sum(b)
    for (a_i, b_i) in AB:
        if sa - a_i <= 0.0:
            continue
        if (sb - b_i) / (sa - a_i) <= b_i / a_i:
            sb -= b_i
            sa -= a_i
    return sb / sa

