from collections import defaultdict

def calculate_max_continuous_correct_submissions(test_cases):
    results = []
    for submissions in test_cases:
        s = 1
        smax = 0
        d = defaultdict(int)
        for (S, R) in submissions:
            if R == 0:
                if smax <= s:
                    smax = s
                s = 0
            elif d[S] == 0:
                s += 1
                d[S] = 1
        if smax <= s:
            smax = s
        results.append(smax)
    return results

