from collections import defaultdict

def calculate_ugliness_after_removal(N, K, A):
    if K == 1:
        return [0] * K
    temp = [A[0]]
    total_ugliness = 0
    for i in range(N - 1):
        if A[i] != A[i + 1]:
            temp.append(A[i + 1])
            total_ugliness += 1
    leni = len(temp)
    dic = defaultdict(int)
    for i in range(1, leni - 1):
        if temp[i - 1] != temp[i + 1]:
            dic[temp[i]] += 1
        else:
            dic[temp[i]] += 2
    dic[temp[0]] -= 1
    dic[temp[-1]] += 1
    result = []
    for i in range(1, K + 1):
        ans = total_ugliness - dic[i]
        result.append(ans)
    return result

