import math

def calculate_candies_eaten(n: int, k: int) -> int:
    s = n * (n + 1) / 2
    discriminant = (2 * n + 3) ** 2 - 4 * (n * (n + 1) - k * 2)
    sqrt_discriminant = math.sqrt(discriminant)
    x1 = int((2 * n + 3 - sqrt_discriminant) // 2)
    x2 = int((2 * n + 3 + sqrt_discriminant) // 2)
    if x1 >= 0 and x2 >= 0:
        return min(x1, x2)
    else:
        return max(x1, x2)

