    k = 1
    for n in range(2, 3328):
        if n & 1:
            k += 1
            A.append(A[-1] + A[-1] / (n - k) // k)
        else:
            A.append(A[-1] + A[-1] * k // (n - k))
    Anums = set(A)
    if N & 1:
        return 'NO'
    else:
        N //= 2
        return 'YES' if N in Anums else 'NO'

