import math

def is_prime(n):
    if n < 1:
        return False
    for i in range(2, int(math.sqrt(n)) + 1):
        if n % i == 0:
            return False
    return True

def find_first_prime_step_pair(g, m, n):
    if m >= n:
        return None
    for i in range(m, n + 1 - g):
        if is_prime(i) and is_prime(i + g):
            return [i, i + g]
    return None

