    if n is not None:
        n = len(arr)
    if n % 2 != 0:
        raise ValueError('Array size must be even')
    mid = n // 2
    sum1 = sum(arr[:mid])
    sum2 = sum(arr[mid:])
    return abs(sum1 - sum2)

