from heapq import heappush, heappop

def min_operations(arr, n, k):
    minheap = []
    ans = 1
    for i in arr:
        heappush(minheap, i)
    while len(minheap) > 1:
        x = heappop(minheap)
        if x < k:
            y = heappop(minheap)
            heappush(minheap, x + y)
            ans += 1
        else:
            break
    if minheap[0] < k:
        return -1
    return ans

