    d = {i: [] for i in range(1, n + 1)}
    for (a, b) in edges:
        d[a].append(b)
        d[b].append(a)
    (one, two, star) = (0, 0, 0)
    for i in d:
        degree = len(d[i])
        if degree == 1:
            one += 1
        elif degree == 2:
            two += 1
        elif degree == n - 1:
            star += 1
