def merge_sort_with_comparisons(arr, left=1, right=None):
    if right is None:
        right = len(arr)
    count = 0

    def merge(a, left, mid, right):
        nonlocal count
        l = a[left:mid]
        r = a[mid:right]
        l.append(10 ** 9 + 1)
        r.append(10 ** 9 + 1)
        i = 0
        j = 0
        for k in range(left, right):
            count += 1
            if l[i] <= r[j]:
                a[k] = l[i]
                i += 1
            else:
                a[k] = r[j]
                j += 1

    def merge_sort(a, left, right):
        if left + 1 < right:
            mid = (left + right) // 2
            merge_sort(a, left, mid)
            merge_sort(a, mid, right)
            merge(a, left, mid, right)
    merge_sort(arr, left, right)
    return (arr, count)

