def find_prime_gap(g, m, n):

    def is_prime(num):
        if num <= 2:
            return False
        for i in range(2, int(num ** 0.5) + 1):
            if num % i == 0:
                return False
        return True
    previous_prime = None
    for i in range(m, n + 1):
        if is_prime(i):
            if previous_prime is not None and i - previous_prime == g:
                return [previous_prime, i]
            previous_prime = i
    return None

