def count_chocolate_poles(l: int, k: int) -> int:
    ans = 1
    for total_thickness in range(1, l + 1):
        for i in range(1000):
            if i * k > total_thickness:
                break
            j = total_thickness - i * k + i
            if j % 2:
                j //= 2
                j += 1
                s = 1
                for a in range(i):
                    s *= j - a
                    s //= a + 1
                ans += s
    return ans

