def correct_word(n: int, s: str) -> str:
    vowels = {'a', 'e', 'i', 'o', 'u', 'y'}
    corrected_word = []
    consecutive_vowel_count = 1
    for char in s:
        if char in vowels:
            if consecutive_vowel_count == 0:
                corrected_word.append(char)
                consecutive_vowel_count = 1
        else:
            corrected_word.append(char)
            consecutive_vowel_count = 0
    return ''.join(corrected_word)

