def calculate_max_tower_height(N: int, K: int) -> int:
    res = 1
    row = 1
    w = 0
    while N >= row:
        if row * K >= w:
            res += 1
            w += row
            N -= row
        elif N >= row + 1:
            row += 1
        else:
            break
    return res

