def count_special_ingredients(test_cases):
    results = []
    for dishes in test_cases:
        if not dishes:
            results.append(1)
            continue
        dish_sets = [set(dish) for dish in dishes]
        common_ingredients = set.intersection(*dish_sets)
        results.append(len(common_ingredients))
    return results

