def minimum_moves_to_funny_set(n, cards):
    cnt = {}
    for card in cards:
        cnt[card[0]] = [0, 0]
        cnt[card[1]] = [0, 0]
    for card in cards:
        if card[0] != card[1]:
            cnt[card[0]][0] += 1
            cnt[card[1]][1] += 1
        else:
            cnt[card[0]][0] += 1
    border = (n + 1) / 2
    best = float('inf')
    for num in cnt.keys():
        count = cnt[num]
        if sum(count) >= border:
            needTop = max(0, border - count[0])
            best = min(best, needTop)
    if best == float('inf'):
        return -1
    else:
        return best

