def calculate_marked_cells(w1, h1, w2, h2):

    def area(w, h):
        return 2 + 2 * h + w

    def mod(n1, n2):
        return abs(n1 - n2)
    if w1 == w2:
        return area(w1, h1) + area(w2, h2) + mod(w1, w2)
    else:
        return area(w1, h1) + area(w2, h2)

