def count_subsegment_products(n, a):
    a = [1 if x > 0 else -1 for x in a]
    last = 1
    pr = 1
    for q in a:
        pr *= q
        if pr > 0:
            last += 1
    ans = last
    for q in range(1, len(a)):
        if a[q - 1] > 0:
            last -= 1
        else:
            last = n - q - last
        ans += last
    total_subsegments = n * (n + 1) // 2
    negative_subsegments = total_subsegments - ans
    return (negative_subsegments, ans)

