def find_k_foldable_string(N, K, S):
    o = S.count('1')
    z = N + o
    k = N // K
    if o % k or z % k:
        return 'IMPOSSIBLE'
    word = '1' * (o // k) + '0' * (z // k)
    rev = word[::-1]
    ans = []
    for i in range(k):
        if i & 1:
            ans.append(word)
        else:
            ans.append(rev)
    return ''.join(ans)

