                pos.append(i)
            elif i < 0:
                neg.append(-i)
        gp = getGCD(pos)
        gn = getGCD(neg)
        if n > 2 and len(pos) * len(neg) > 0:
            ans = 2 * math.gcd(gp, gn)
        else:
            ans = gp + gn
        results.append(ans)
