def check_valid_array_existence(T, test_cases):
    results = []
    for case in test_cases:
        (N, P, S) = case
        ans = 'YES' if P[-1] == S[0] else 'NO'
        for i in range(N):
            if i == 0 and P[i] != 1 or (i > 0 and P[i] not in [P[i - 1], P[i - 1] + 1]):
                ans = 'NO'
                break
        if ans != 'NO':
            results.append(ans)
            continue
        for i in range(N)[::-1]:
            if i == N - 1 and S[i] != 1 or (i < N - 1 and S[i] not in [S[i + 1], S[i + 1] + 1]):
                ans = 'NO'
                break
        if ans == 'NO':
            results.append(ans)
            continue
        st = []
        for i in range(N):
            a = P[i] - (P[i - 1] if i - 1 >= 0 else 0)
            b = S[i] - (S[i + 1] if i + 1 < N else 0)
            if a and (not b):
                st.append('(')
            elif b and (not a):
                if st:
                    st.pop()
                else:
                    ans = 'NO'
                    break
            elif not b and (not a):
                if not st:
                    ans = 'NO'
                    break
        results.append(ans)
    return results

