from math import factorial

def ncr(n, r):
    if n > r:
        return int(factorial(n) / (factorial(r) * factorial(n - r)))
    return 0

def count_integers_with_same_set_bits(N):
    bina = list(map(int, bin(N)[2:]))
    res = 0
    for i in range(len(bina)):
        if bina[i] == 1:
            r = sum(bina[i:])
            n = len(bina) - 1 - i
            res = res + ncr(n, r)
    return res

