def is_almost_palindrome(s: str) -> bool:

    def is_palindrome(sub_s: str) -> bool:
        return sub_s == sub_s[::-1]
    (left, right) = (0, len(s) - 1)
    while left <= right:
        if s[left] != s[right]:
            return is_palindrome(s[left:right]) or is_palindrome(s[left + 1:right + 1])
        left += 1
        right -= 1
    return True

