def generate_hollow_triangle(height):
    width = height * 2 + 1

    def mid(n):
        return '#' if n == 1 else '#{}#'.format('_' * (n - 2))
    rows = []
    for i in range(1, width, 2):
        row = '{{:_^{}}}'.format(width).format(mid(i))
        rows.append(row)
    rows.append(width * '#')
    return rows

