def min_traffic_controllers(n, m, roads):
    table = [(u - 1, v - 1, c) for (u, v, c) in roads]

    def check(k):
        Lin = [0] * n
        edge = [[] for _ in range(n)]
        for (s, t, c) in table:
            if c > k:
                Lin[t] += 1
                edge[s].append(t)
        Haco = deque()
        ans = []
        for i in range(n):
            if Lin[i] == 0:
                ans.append(i)
                Haco.append(i)
        while Haco:
            x = Haco.pop()
            for y in edge[x]:
                Lin[y] -= 1
                if Lin[y] == 0:
                    ans.append(y)
                    Haco.append(y)
        return ans
    ma = 10 ** 9 - 7
    mi = -1
    while ma - mi > 1:
        mid = (ma + mi) // 2
        if len(check(mid)) == n:
            ma = mid
        else:
            mi = mid
    ans = check(ma)
    dd = {}
    for i in range(n):
        dd[ans[i]] = i
    num = 0
    answer = []
    for i in range(m):
        (s, t, c) = table[i]
        if dd[s] > dd[t] and c <= ma:
            answer.append(i + 1)
            num += 1
    return (ma, answer)

