    x = {a: b for (a, b) in beacons}
    mm = max(x.keys())
    otv = [0]
    ma = 1
    for i in range(1, mm + 2):
        if i not in x:
            otv.append(otv[i - 1])
            ma = max(ma, otv[i - 1])
